import { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';

const UserProfile = ({ username }) => {
  const [userInfo, setUserInfo] = useState(null);
  const [posts, setPosts] = useState([]);
  const [description, setDescription] = useState('');
  const [isEditingDescription, setIsEditingDescription] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user: currentUser } = useAuth();

  useEffect(() => {
    loadUserData();
  }, [username]);

  const loadUserData = async () => {
    try {
      // Load user info
      const userResponse = await fetch(`/api/users/${username}`, {
        credentials: 'include'
      });
      if (!userResponse.ok) throw new Error('Failed to load user info');
      const userData = await userResponse.json();
      setUserInfo(userData);
      setDescription(userData.description || '');

      // Load user's posts
      const postsResponse = await fetch(`/api/posts/user/${username}`, {
        credentials: 'include'
      });
      if (!postsResponse.ok) throw new Error('Failed to load posts');
      const postsData = await postsResponse.json();
      setPosts(postsData);
    } catch (err) {
      setError(err.message);
      console.error('Error loading user data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveDescription = async () => {
    try {
      const response = await fetch('/api/users/description', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ description }),
      });

      if (!response.ok) throw new Error('Failed to update description');

      setUserInfo(prev => ({
        ...prev,
        description
      }));
      setIsEditingDescription(false);
    } catch (err) {
      console.error('Error updating description:', err);
    }
  };

  const handleDeletePost = async (postId) => {
    try {
      const response = await fetch(`/api/posts/${postId}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to delete post');

      setPosts(posts.filter(post => post._id !== postId));
    } catch (err) {
      console.error('Error deleting post:', err);
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  if (error) {
    return <div className="text-center py-8 text-red-500">{error}</div>;
  }

  if (!userInfo) {
    return <div className="text-center py-8">User not found</div>;
  }

  return (
    <div className="max-w-2xl mx-auto p-4">
      {/* User Header */}
      <div className="bg-white p-6 rounded-lg shadow mb-6">
        <h1 className="text-3xl font-bold text-gray-900">{username}</h1>
        <p className="text-gray-500 mt-2">
          Joined {new Date(userInfo.joinDate).toLocaleDateString()}
        </p>

        {/* Description */}
        <div className="mt-4">
          {isEditingDescription && currentUser?.username === username ? (
            <div>
              <textarea
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="w-full p-2 border rounded-lg resize-none focus:ring-2 focus:ring-blue-500"
                rows="3"
                placeholder="Add a description..."
              />
              <div className="mt-2 space-x-2">
                <button
                  onClick={handleSaveDescription}
                  className="px-3 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                >
                  Save
                </button>
                <button
                  onClick={() => setIsEditingDescription(false)}
                  className="px-3 py-1 bg-gray-500 text-white rounded-md hover:bg-gray-600"
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <div>
              <p className="text-gray-700">
                {userInfo.description || 'No description yet.'}
              </p>
              {currentUser?.username === username && (
                <button
                  onClick={() => setIsEditingDescription(true)}
                  className="mt-2 text-blue-500 hover:text-blue-700"
                >
                  Edit description
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* User's Posts */}
      <div className="space-y-4">
        {posts.map(post => (
          <div key={post._id} className="bg-white p-4 rounded-lg shadow">
            <div className="flex justify-between items-start">
              <span className="font-medium text-gray-900">{post.username}</span>
              <span className="text-gray-500 text-sm">
                {new Date(post.createdAt).toLocaleString()}
              </span>
            </div>
            <p className="mt-2 text-gray-700">{post.content}</p>
            {currentUser?.username === username && (
              <div className="mt-3">
                <button
                  onClick={() => handleDeletePost(post._id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserProfile;
